import { useState, useEffect } from "react";

export default (url) => {
  const [stats, setStats] = useState();
  useEffect(() => {
    async function fetchData() {
      console.log("Fetching Data");
      const data = await fetch(url).then(data =>
        data.json()
      );
      setStats(data);
    }
    fetchData();
  }, []);
  return stats;
};
