import useStats from "../utils/useStats";
import Stats from "./Stats";
import { useState } from "react";
const url = `https://covid19.mathdro.id/api/countries`;

export default () => {
  const { stats: countries, error, loading } = useStats(url);
  const [selectedCountry, setSelectedCountry] = useState("USA");
  if (!countries) return <p>Loading Countries...</p>;
  return (
    <div>
      <h2>Currently showing: {selectedCountry}</h2>
      <select
        onChange={e => {
          setSelectedCountry(e.target.value);
        }}
      >
        {Object.entries(countries.countries).map(([country, countryCode]) => {
          return (
            <option key={countryCode} value={countries.iso3[countryCode]} selected>
              {country}
            </option>
          );
        })}
      </select>
      <Stats
        url={`https://covid19.mathdro.id/api/countries/${selectedCountry}`}
      ></Stats>
    </div>
  );
};
