import { useState, useEffect } from "react";

export default url => {
  console.log(url);
  const [stats, setStats] = useState();
  useEffect(() => {
    async function fetchData() {
      console.log("Updating or mounting");
      console.log("Fetching Data");
      const data = await fetch(url).then(res => res.json());
      setStats(data);
    }
    fetchData();
  }, [url]);
  return stats;
};
