import { useState, useEffect } from "react";

export default () => {
  const [stats, setStats] = useState();
  useEffect(() => {
    async function fetchData() {
      console.log("Fetching Data");
      const data = await fetch(`https://covid19.mathdro.id/api`).then(data =>
        data.json()
      );
      setStats(data);
    }
    fetchData();
  }, []);
  return stats;
};
