import { useState, useEffect } from "react";

const useStats = () => {
  const [stats, setStats] = useState();
  useEffect(() => {
    async function fetchData() {
      console.log("Fetching Data");
      const data = await fetch(`https://covid19.mathdro.id/api`).then(data =>
        data.json()
      );
      setStats(data)
    }
    fetchData();
  }, []);
  return stats
};
const Stats = () => {
  const stats = useStats();
  if(!stats) return <p></p>
  return (
    <div>
      <p>Stats:</p>
    </div>
  );
};
export default function IndexPage() {
  return (
    <div>
      <Stats></Stats>
    </div>
  );
}
